import React, {useState, useEffect, useRef} from 'react';
import './App.css';

function App() {
  const [level, setLevel] = useState(0)
  const [numString, setNumString] = useState("")
  const [isTimeRunning, setIsTimeRunning] = useState(false)
  const [text, setText] = useState("")
  const [answer, setAnswer] = useState("")
  const [isGameRunning, setIsGameRunning] = useState(false)
  const textBoxRef = useRef(null)

  function handleChange(e) {
    const {value} = e.target
    setText(value)
}


  function generateRandomNumber() {
    let tempNumString = numString
    tempNumString = tempNumString + (Math.floor(Math.random() * 10)).toString()
    setLevel(level+1)
    setNumString(tempNumString)
    setIsTimeRunning(true)
  }

  function resetGame(){
    setLevel(0)
    setNumString("")
    setIsTimeRunning(false)
    setText("")
    setAnswer("")
    setIsGameRunning(false)
  }

  function startGame(){
    generateRandomNumber()
    setIsGameRunning(true)
    textBoxRef.current.disabled = false
    textBoxRef.current.focus()
  }

  function submitAnswer(){
    console.log("Answer submitted")
    console.log(text)
    console.log(numString)
    setText("")
    text === numString ? generateRandomNumber() : resetGame()
  }


  return (
    <div className="App">
      <h1>Check your memory</h1>
      <h3>Level: {level}</h3>
      <button onClick={startGame} disabled={isGameRunning}>Start Game</button>
      <p>{isTimeRunning ? numString: ""}</p>
      <textarea onChange={handleChange} value={text} ref={textBoxRef}/><br />
      <button onClick={submitAnswer}>Submit Answer</button>
      <button onClick={resetGame}>Reset Game</button>
    </div>
  );
}

export default App;
