import React, {useState, useEffect} from "react"
import { Link } from 'react-router-dom'
import axios from 'axios'

const Score = props => (
    <tr>
        <td>{props.scoresusername}</td>
        <td>{props.scores.score}</td>
    </tr>
)

function Scoreboard() {
    const [score, setScore] = useState({scores: []})

    useEffect(() => {
        axios.get('http://localhost:5000/scores/')
        .then(response => {
            this.setState ({ scores: response.data})
        })
        .catch((error) => {
            console.log(error)
        })

    },[])
    

    const ScoreList = (() => {
        return score.scores.map(currentScore => {
            return <Score score={currentScore.score} key={currentScore._id}></Score>
        })
    }
    )
    return(
        <div>
            {ScoreList}

        </div>
    )
}

export default Scoreboard
